<!-- add this near the end of the file, right before </body> -->
<script>
  (function(){
    const imgs = document.querySelectorAll('img[src]');
    imgs.forEach(img => {
      try {
        const src = img.getAttribute('src');
        if (!src) return;
        // skip data URIs and already-correct paths
        if (src.startsWith('data:') || src.includes('assets/images/')) return;
        // keep absolute urls (http/https) untouched
        if (/^https?:\/\//i.test(src)) return;
        // get filename and update to assets/images/<filename>
        const filename = src.split('/').pop();
        if (filename) img.src = 'assets/images/' + filename;
      } catch (e) { /* noop */ }
    });
  })();
</script>
